import React from 'react';
import Cover from '../components/Cover';
import Banner from '../components/Banner';
import { Link, RouteComponentProps } from 'react-router-dom';
import RoomsContainer from '../containers/RoomsContainer';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css'

interface IState {
    startDate: Date; 
    endDate: Date; 
    noOfPeople: number; 
}

export default class RoomSelection extends React.Component<RouteComponentProps, IState> {
    constructor(props: RouteComponentProps) {
        super(props);


        this.state = {
            startDate: new Date(),
            endDate: new Date(),
            noOfPeople: 1
        }
    }

    componentDidMount() {
        setTimeout(() => {
            const { startDate, endDate, noOfPeople } = this.props.location.state;
        console.log(startDate);
        console.log(endDate);
        console.log(noOfPeople);

        this.setState({
            startDate: startDate,
            endDate: endDate,
            noOfPeople: noOfPeople
        })
        console.log(this.state.noOfPeople);
        }, 1000)
    }

    public render(): JSX.Element {
        const options = [
            { value: 'one', label: 'One' },
            { value: 'two', label: 'Two', className: 'myOptionClassName' },
          ]
        const defaultOption = options[0]
        return <div>
        <Cover coverClass="roomsHero">
            <Banner 
            title="our rooms"
            >
            <Link to="/" className="btn-primary">return home</Link>
            </Banner>
        </Cover>
        <Dropdown options={options} value={defaultOption} placeholder="Select an option" />
        <RoomsContainer />
        </div>
    }
}
