import React from 'react';
import Cover from '../components/Cover';
import Banner from '../components/Banner';
import { Link } from 'react-router-dom';
import Services from '../components/Services';
import FeaturedRooms from '../components/FeaturedRooms';
import DatePicker from 'react-datepicker';
import NumericInput from "react-numeric-input";



interface IState {
    startDate: Date;
    endDate: Date;
    noOfPeople: number;
}

export default class Home extends React.Component<{}, IState> {
    constructor(props: {}) {
        super(props);

        this.state = {
            startDate: new Date(),
            endDate: new Date(), 
            noOfPeople: 1
        }
        this.handleNoOfPeopleChange = this.handleNoOfPeopleChange.bind(this);
    }

    handleStartDateChange = (date: any) => {
        this.setState({
            startDate: date
        });
        console.log(this.state.startDate.getDate.toString);
    };

    handleEndDateChange = (date: any) => {
        this.setState({
            endDate: date
        });
    };

    handleNoOfPeopleChange(new_value: any) {
        this.setState({
            noOfPeople: new_value
        });
    };

    public render(): JSX.Element {
        console.log(this.state);
        return <div>
        <Cover>
            <Banner 
            title="Alexis Suites"
            subtitle="Indulging a premium & smart stay"
            >
                Start date: <DatePicker 
                selected={this.state.startDate}
                onChange={this.handleStartDateChange}/> 
                End date: <DatePicker 
                selected={this.state.endDate}
                onChange={this.handleEndDateChange}/>
                People: <NumericInput min={1} max={5}
                onChange={this.handleNoOfPeopleChange}/>
            <Link to="/rooms" className="btn-primary">book now</Link>
            </Banner>
        </Cover>
        <Services/>
        <FeaturedRooms />
    </div>
    }
}
