import React, { Props } from 'react';
import { Link } from 'react-router-dom';
import { ReactComponent } from '*.svg';
import { IState } from '../models/models';

interface LState {
    startDate: Date; 
    endDate: Date; 
    noOfPeople: number; 
    roomType: string;
    fname: string; 
    lname: string;
    email: string;
    phoneno: string;
}

export class BookingForm extends React.Component<LState, IState> {
    constructor(props: Readonly<LState>) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this)

        this.state = {
            startDate: new Date(), 
            endDate: new Date(),
            noOfPeople: 1, 
            roomType: "",
            fname: "",
            lname: "", 
            email: "", 
            phoneno: ""
        }
    }

    componentDidMount() {
        setTimeout(() => {
            //const { startDate, endDate, noOfPeople, roomType } = this.props.location.state;
        console.log(startDate);
        console.log(endDate);
        console.log(noOfPeople);
        console.log(roomType);

        this.setState({
            startDate: this.props.startDate,
            endDate: this.props.endDate,
            noOfPeople: this.props.noOfPeople,
            roomType: this.props.roomType,
            fname: this.props.fname,
            lname: this.props.lname,
            email: this.props.email,
            phoneno: this.props.phoneno
        })
        console.log(this.state.noOfPeople);
        console.log(this.state.roomType);
        }, 1000)
    }

    public handleSubmit(e: React.FormEvent<HTMLFormElement>): void {
        e.preventDefault();
        this.setState({
            fname: (this.refs.fname as HTMLInputElement).value, 
            lname: (this.refs.lname as HTMLInputElement).value,
            email: (this.refs.email as HTMLInputElement).value, 
            phoneno: (this.refs.phoneno as HTMLInputElement).value
        })
        console.log(this.state.fname);
    }

    public render(): JSX.Element {
        console.log(this.state);
        return <div>
            <form onSubmit={(e) => this.handleSubmit(e)} noValidate={true}>
            <div className="container">
                <div className="form-group banner">
                    <p>First Name: <input ref='fname' placeholder= "Enter first name" type="text"></input> Last Name: <input ref='lname' placeholder= "Enter last name" type="text"></input></p>
                    <p>Email address: <input ref='email' placeholder="Enter email address" type="email"></input></p>
                    <p>Phone number: <input ref='phoneno' placeholder="Enter phone number" type="text"></input></p>
                    <button className="btn-primary" type="submit">Login</button>
                </div>
            </div>
        </form>
        </div>
    }
}

export default BookingForm;
